const types = Object.keys(categoryMap);

const makePhotoshootArray = (gender: "female" | "male"): PhotoshootType[] => {
    // Filter out specific types for male gender
    const filteredTypes = gender === "male" 
        ? types.filter(type => !["Pixie Cut", "Bridal Elegance", "K-pop Idol"].includes(type))
        : types;
    
    return filteredTypes.map((typeKey) => {
      const key = typeKey.toLowerCase().replace(/ & /g, "_").replace(/\s+/g, "_");
  
      return {
        type: typeKey,
        mainImage: `/assets/images/models/${gender}/${key}.png`,
        additionalImages: [
          `/assets/images/models/${gender}/${key}_1.png`,
          `/assets/images/models/${gender}/${key}_2.png`,
          `/assets/images/models/${gender}/${key}_3.png`,
        ],
        category: categoryMap[typeKey],
        prompts: {
          female: promptMap[typeKey]?.female || ["", "", ""],
          male: promptMap[typeKey]?.male || ["", "", ""],
        },
      };
    });
};
  
const PhotoshootTypeData: PhotoshootData = {
    Female: makePhotoshootArray("female"),
    Male: makePhotoshootArray("male"),
};
  
export default PhotoshootTypeData;